<!DOCTYPE html><head>	<meta charset="utf-8">	<title>AdminPanel</title>	<link media="all" rel="stylesheet" type="text/css" href="/css/admin_panel.css" />	<script type="text/javascript" src="//ajax.googleapis.com/ajax/libs/jquery/1.7.2/jquery.min.js"></script>	<script type="text/javascript">window.jQuery || document.write('<script type="text/javascript" src="js/jquery-1.7.2.min.js"><\/script>');</script>    <!--<script type="text/javascript" src="js/jquery.main.js"></script>-->	<!--[if lt IE 9]><link rel="stylesheet" type="text/css" href="css/ie.css" /><![endif]--></head><body>	<div id="wrapper">		<div id="content">			<div class="c1">				<div class="controls">					{% block navLinks %}					<nav class="links">						<ul>							<li class="active"><a href="#" class="ico1">Messages <span class="num">26</span></a></li>							<li><a href="#" class="ico2">Alerts <span class="num">5</span></a></li>							<li><a href="#" class="ico3">Documents <span class="num">3</span></a></li>						</ul>					</nav>					{% endblock %}					<div class="profile-box">						<span class="profile">							<a href="#" class="section">								<img class="image" src="/images/img1.png" alt="image description" width="26" height="26" />								<span class="text-box">									Welcome									<strong class="name">SuperAdmin</strong>								</span>							</a>							<a href="#" class="opener">opener</a>						</span>						<a href="{{url.generate('adminLogout')}}" class="btn-on">On</a>					</div>				</div>				<div class="tabs">					{% block content %}					<div id="tab-1" class="tab">						<article>							<div class="text-section">								<h1>Dashboard</h1>								<p>This is a quick overview of some features</p>							</div>							<ul class="states">								<li class="error">Error : This is an error placed text message.</li>								<li class="warning">Warning: This is a warning placed text message.</li>								<li class="succes">Succes : This is a succes placed text message.</li>							</ul>						</article>					</div>					{% endblock %}				</div>			</div>		</div>		{% block asideSidebar %}		<aside id="sidebar">			<strong class="logo"><a href="#">lg</a></strong>			<ul class="tabset buttons">				<li {% if navIndex %}class="active" {% endif %}>					<a href="{{url.generate('adminMain')}}" class="ico1"><span>Dashboard</span><em></em></a>					<span class="tooltip"><span>Dashboard</span></span>				</li>				<li {% if navNews %}class="active" {% endif %}>					<a href="{{url.generate('adminNews')}}" class="ico2"><span>Новости</span><em></em></a>					<span class="tooltip"><span>Новости</span></span>				</li>				<li {% if navPages %}class="active" {% endif %}>					<a href="{{url.generate('adminPages')}}" class="ico3"><span>Pages</span><em></em></a>					<span class="tooltip"><span>Pages</span></span>				</li>				<div style="display:none;">				<li>					<a href="#" class="ico4"><span>Widgets</span><em></em></a>					<span class="tooltip"><span>Widgets</span></span>				</li>				<li>					<a href="#" class="ico5"><span>Archive</span><em></em></a>					<span class="tooltip"><span>Archive</span></span>				</li>				<li>					<a href="#" class="ico6">						<span>Comments</span><em></em>					</a>					<span class="num">11</span>					<span class="tooltip"><span>Comments</span></span>				</li>				<li>					<a href="#" class="ico7"><span>Plug-in</span><em></em></a>					<span class="tooltip"><span>Plug-in</span></span>				</li>				</div>				<li>					<a href="#" class="ico8"><span>Settings</span><em></em></a>					<span class="tooltip"><span>Settings</span></span>				</li>			</ul>			<span class="shadow"></span>		</aside>		{% endblock %}	</div></body></html>